using UnityEditor;[CustomEditor(typeof(GN_GameController))]public class GN_GameControllerEditor : Editor {    string module = "Game Controller";    void Awake() {        GN_Editor.GetLogo();    }    public override void OnInspectorGUI() {        GN_Editor.DefineGUIStyle(module);        EditorGUILayout.BeginVertical("box");        DrawDefaultInspector();        EditorGUILayout.EndHorizontal();    }}