using UnityEditor;[CustomEditor(typeof(GN_LevelSelection))]public class GN_LevelSelectionEditor : Editor {    string module = "Level Selection";    void Awake() {        GN_Editor.GetLogo();    }    public override void OnInspectorGUI() {        GN_Editor.DefineGUIStyle(module);        EditorGUILayout.BeginVertical("box");        DrawDefaultInspector();        EditorGUILayout.EndHorizontal();    }}