using UnityEditor;[CustomEditor(typeof(GN_PlayerSelection))]public class GN_PlayerSelectionEditor : Editor{    string module = "Player Selection";    void Awake() {        GN_Editor.GetLogo();    }    public override void OnInspectorGUI() {        GN_Editor.DefineGUIStyle(module);        EditorGUILayout.BeginVertical("box");        DrawDefaultInspector();        EditorGUILayout.EndHorizontal();    }}